import React from "react"

export class SubMenu extends React.Component {
    render() {
        let textColor = "";
        if (this.props.level === "easy") textColor = "chartreuse";
        if (this.props.level === "medium") textColor = "#ffde00";
        if (this.props.level === "hard") textColor = "";
        if (this.props.level === "crazy") textColor = "";
        if (this.props.level === "insane") textColor = "";
        return (
            <div className="sub-menu">
                <div className="sub-menu-score">
                    Score: {this.props.score}
                </div>
                <div className="sub-menu-level"> 
                    Level: <div style={{color: textColor}}>{this.props.level}</div>
                </div>
                <button
                    className="sub-menu-btn"
                    onClick={this.props.goToMainMenu}>
                    Main menu
                </button>
            </div>
        )
    }
}
