import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import {Field} from "./Field";

class Snake extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            field: [],
            xSize: 10,
            ySize: 10,
            headX: 7,
            headY: 5,
            snakeLength: 3,
            direction: "left",
            appleX: 2,
            appleY: 1
        };
    }

    generateApple() {
        while (this.state.field[this.state.appleX][this.state.appleY].className === "snake") {
            this.setState({
                appleX: Math.round(Math.random() * this.state.xSize),
                appleY: Math.round(Math.random() * this.state.ySize)
            });
        }
    }

    // componentDidMount() {
    //     this.drawField();
    //     this.timerID = setInterval(
    //         () => {this.move();
    //                 this.drawField()},
    //         1000
    //     )
    // }
    //
    // componentWillUnmount() {
    //     clearInterval(this.timerID);
    // }

    move() {
        switch (this.state.direction) {
            case "left":
                this.setState({
                    headX: this.state.headX - 1
                });
                break;
            case "right":
                this.setState({
                    headX: this.state.headX + 1
                });
                break;
            case "up":
                this.setState({
                    headX: this.state.headY - 1
                });
                break;
            case "down":
                this.setState({
                    headX: this.state.headY + 1
                });
                break;
        }
    }

    drawField() {
            let data = [];
            let className = "";
            for (let y = 0; y < this.state.ySize; y++) {
                data[y] = [];
                for (let x = 0; x < this.state.xSize; x++) {
                    if (x === this.state.headX && y === this.state.headY) {
                        className = "snake"
                    } else if (x === this.state.appleX && y === this.state.appleY) {
                        className = "apple"
                    } else {
                        className = "pixel"
                    }
                    data[y].push({key: x + "" + y, className: className, value: x + ":" + y});
                }
            }
            this.setState({
                field: this.state.field.concat(data)
            });
    }

    start() {
        setInterval(() => {
            this.move();
            this.drawField();
        }, 10)
    }

    render() {
        return (
            <div className="wrapper">
                <Field field={this.state.field}/>
                <button className="start-btn" onClick={this.start.bind(this)}>START</button>
                <button className="start-btn" onClick={this.move.bind(this)}>MOVE</button>
            </div>

        )
    }
}

ReactDOM.render(<Snake/>, document.getElementById("root"));
