import React from 'react';

export class GameOver extends React.Component {
    render() {
        let lastWords = "";
        if (this.props.score === 0) lastWords = "Hmmm. Try to move on the... over side";
        if (this.props.score > 0 && this.props.score <= 10) lastWords = "Have you done your homework?";
        if (this.props.score > 10 && this.props.score <= 25) lastWords = "I believe you can better!";
        if (this.props.score > 25 && this.props.score <= 40) lastWords = "Not bad, bro! Not bad!";
        if (this.props.score > 40 && this.props.score <= 50) lastWords = "Are you human?";
        if (this.props.score > 50) lastWords = "I`ll call you master!";

        return (
            <div className={"gameover"} style={{zIndex: this.props.zIndex}}>
                <h2 className="gameover-lastwords">{lastWords}</h2>
                <h3 className="gameover-score">Your score: {this.props.score}</h3>
                <h1 className="gameover-title">Game Over</h1>
                <button className="gameover-restart" onClick={this.props.restart}>Restart</button>
                <div className="explanation">Press Space</div>
            </div>
        )
    }
}
