import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import {Field} from "./Field";

class Snake extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            field: [],
            xSize: 10,
            ySize: 10,
            headX: 7,
            headY: 5,
            snakeLength: 3,
            direction: "left",
            appleX: 2,
            appleY: 1
        };
    }

    generateApple() {
        while (this.state.field[this.state.appleX][this.state.appleY].className === "snake") {
            this.setState({
                appleX: Math.round(Math.random() * this.state.xSize),
                appleY: Math.round(Math.random() * this.state.ySize)
            });
        }
    }

    move() {
        switch (this.state.direction) {
            case "left":
                this.setState({
                    headX: this.state.headX - 1
                });
                break;
            case "right":
                this.setState({
                    headX: this.state.headX + 1
                });
                break;
            case "up":
                this.setState({
                    headX: this.state.headY - 1
                });
                break;
            case "down":
                this.setState({
                    headX: this.state.headY + 1
                });
                break;
        }
    }

    componentDidMount() {
        this.move();
    }

    drawField() {
        if (this.state.field.length < this.state.xSize.toFixed()) {
            let data = [];
            let className = "";
            for (let y = 0; y < this.state.ySize; y++) {
                data[y] = [];
                for (let x = 0; x < this.state.xSize; x++) {
                    if (x === this.state.headX && y === this.state.headY) {
                        className = "snake"
                    } else if (x === this.state.appleX && y === this.state.appleY) {
                        className = "apple"
                    } else {
                        className = "pixel"
                    }
                    data[y].push({key: x + "" + y, className: className, value: x + ":" + y});
                }
            }
            this.setState({
                field: this.state.field.concat(data)
            });
        }
    }

    render() {
        this.drawField.bind(this);
        this.generateApple.bind(this);
        return (
            <div className="wrapper">
                <Field field={this.state.field}/>
                <button className="start-btn" onClick={this.drawField.bind(this)}>START</button>
            </div>

        )
    }
}

setInterval(ReactDOM.render(<Snake/>, document.getElementById("root")), 1000);
