import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import {Field} from "./Field";

class Snake extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            field: [],
            xSize: 10, ySize: 10,
            headX: 9, headY: 5,
            snakeLength: 1,
            snake: {x: [9], y: [5], length: 1},
            direction: "",
            appleX: 2, appleY: 1,
            isStarted: false,
            isWallsTransparent: false,
            gameOver: false,
            isPaused: false
        };
    }


    componentDidMount() {
        document.addEventListener("keydown", this.changeDirection);
        document.addEventListener("keydown", this.pause);
    }

    generateApple() {
        const newAppleX = Math.round(Math.random() * (this.state.xSize - 1));
        const newAppleY = Math.round(Math.random() * (this.state.ySize - 1));
        if (!this.isSnake(newAppleX, newAppleY)) {
            this.setState({
                appleX: newAppleX,
                appleY: newAppleY
            });
        } else {
            this.generateApple();
        }
    }

    changeDirection = (event) => {
        if (event.keyCode === 37 && this.state.direction !== "right") {
            this.setState({
                direction: "left"
            });
        }
        if (event.keyCode === 38 && this.state.direction !== "down") {
            this.setState({
                direction: "up"
            });
        }
        if (event.keyCode === 39 && this.state.direction !== "left") {
            this.setState({
                direction: "right"
            });
        }
        if (event.keyCode === 40 && this.state.direction !== "up") {
            this.setState({
                direction: "down"
            });
        }
    };

    move() {
        switch (this.state.direction) {
            case "left":
                this.setState({
                    headX: this.state.headX - 1
                });
                break;
            case "right":
                this.setState({
                    headX: this.state.headX + 1
                });
                break;
            case "up":
                this.setState({
                    headY: this.state.headY - 1
                });
                break;
            case "down":
                this.setState({
                    headY: this.state.headY + 1
                });
                break;
            default:
                this.setState({
                    headX: this.state.headX,
                    headY: this.state.headY,
                });
        }

        this.setState({
            isPaused: false
        });
        this.cannibal();

        if (this.state.headX === this.state.appleX && this.state.headY === this.state.appleY) {
            this.setState({
                snakeLength: this.state.snakeLength + 1
            });
            this.state.snake.length = this.state.snakeLength;
            this.generateApple();
        }

        if (this.state.isWallsTransparent) {
            if (this.state.headX < 0) this.setState({headX: this.state.xSize - 1});
            if (this.state.headX > this.state.xSize - 1) this.setState({headX: 0});
            if (this.state.headY < 0) this.setState({headY: this.state.ySize - 1});
            if (this.state.headY > this.state.ySize - 1) this.setState({headY: 0});
        } else {
            if (this.state.headX < 0 || this.state.headX > this.state.xSize - 1 ||
                this.state.headY < 0 || this.state.headY > this.state.ySize - 1) {
                this.stop();
                this.setState({gameOver: true});
            }
        }
    }

    cannibal() {
        for (let i = 1; i < this.state.snake.length; i++) {
            if (this.state.snake.x[i] === this.state.headX && this.state.snake.y[i] === this.state.headY) {
                this.stop();
                this.setState({gameOver: true});
            }
        }
    }

    isSnake(x, y) {
        for (let i = 0; i < this.state.snake.length; i++) {
            if (this.state.snake.x[i] === x && this.state.snake.y[i] === y) return true;
        }
        return false;
    }

    makeSnake() {
        for (let i = this.state.snake.length - 1; i >= 0; i--) {
            if (i > 0) {
                this.state.snake.x[i] = this.state.snake.x[i-1];
                this.state.snake.y[i] = this.state.snake.y[i-1];
            }
            if (i === 0) {
                this.state.snake.x[i] = this.state.headX;
                this.state.snake.y[i] = this.state.headY;
            }
        }
    }

    drawField() {
        let data = [];
        let className = "";
        for (let y = 0; y < this.state.ySize; y++) {
            data[y] = [];
            for (let x = 0; x < this.state.xSize; x++) {
                if (this.isSnake(x, y)) {
                    if (x === this.state.headX && y === this.state.headY) {
                        className = "head"
                    } else {
                        className = "snake"
                    }
                } else if (x === this.state.appleX && y === this.state.appleY) {
                    className = "apple"
                } else {
                    className = "pixel"
                }
                data[y].push({key: x + "" + y, className: className, value: x + ":" + y});
            }
        }
        this.setState({
            field: this.state.field.concat(data)
        });
    }

    start() {
        if (!this.state.isStarted) {
            this.timerID = setInterval(() => {
                this.setState({
                    gameOver: false,
                    field: [],
                    isStarted: true,
                });
                this.move();
                this.makeSnake();
                this.drawField();
            }, 500)
        }
    }

    stop() {
        if (this.state.isStarted) {
            clearInterval(this.timerID);
            this.setState({
                isStarted: false
            });
        }
    }

    pause = (event) => {
        if (event.keyCode === 32) {
            this.setState({
                isPaused: !this.state.isPaused
            });
        }
        if(this.state.isPaused) {
            this.setState({
                direction: ""
            });
        }
    };

    setDefault() {
        this.setState({
            headX: 9, headY: 5,
            snakeLength: 1,
            snake: {x: [9], y: [5], length: 1},
            direction: "",
            appleX: 2, appleY: 1,
            isStarted: false,
        });
    }

    restart() {
        this.setDefault();
        this.start();
    }

    render() {
        return (
            <div className="wrapper" onKeyDown={this.changeDirection.bind(this)}>
                <Field
                    field={this.state.field}
                    gameOver={this.state.gameOver}
                    score={this.state.snakeLength - 1}
                    restart={this.restart.bind(this)}
                    start={this.start.bind(this)}
                    isStarted={this.state.isStarted}/>
                <p className="score">Score: {this.state.snakeLength - 1}</p>
            </div>

        )
    }
}

ReactDOM.render(<Snake/>, document.getElementById("root"));
