import React from 'react';
import {Pixel} from "./Pixel";
import {GameOver} from "./GameOver";
import {MainMenu} from "./Starter";

export class Field extends React.Component {
    render() {
        let field = [];
        if (this.props.screen === "mainmenu") {
            field = <MainMenu start={this.props.start}/>
        }
        if (this.props.screen === "gameover") {
            field = <GameOver score={this.props.score} restart={this.props.restart}/>
        }
        if (this.props.screen === "game") {
            field = this.props.field.map((pixelX) =>
                pixelX.map((pixelY) =>
                    <Pixel key={pixelY.key} className={pixelY.className} value={pixelY.value}/>)
            )
        }
        return (
            <div className="field">
                {field}
            </div>
        )
    }
}
