import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import {Pixel} from "./Pixel";

class Field extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            field: [],
            xSize: 10,
            ySize: 10,
            headX: 5,
            headY: 7,
            snakeLength: 3,
            direction: "left",
            appleX: 0,
            appleY: 0
        };
    }

    generateApple() {
        while(this.state.field[this.state.appleX][this.state.appleY].className === "snake") {
            this.setState({
                appleX: Math.round(Math.random()*this.state.xSize),
                appleY: Math.round(Math.random()*this.state.ySize)
            });
        }
    }

    drawField() {
        this.generateApple();
        let data = [];
        let className = "";
        for (let x = 0; x < this.state.xSize; x++) {
            data[x] = [];
            for (let y = 0; y < this.state.ySize; y++) {
                if (x === this.state.headX && y === this.state.headY) {
                    className = "snake"
                } else if (x === this.state.appleX && y === this.state.appleY) {
                    className = "apple"
                } else {
                    className = "pixel"
                }
                data[x].push({key: x + "" + y, className: className});
            }
        }
        this.setState({
            field: this.state.field.concat(data)
        });
    }

    render() {

        const field = this.state.field.map((pixelX) =>
            pixelX.map((pixelY) =>
                <Pixel key={pixelY.key} className={pixelY.className}/>)
        );
        return (
            <div className="wrapper">
                <div className="field">
                    {field}
                </div>
                <button className="start-btn" onClick={this.drawField.bind(this)}>START</button>
            </div>

        )
    }
}

ReactDOM.render(<Field/>, document.getElementById("root"));
